const About = () => (
  <div className="p-4 max-w-xl mx-auto">
    <h2 className="text-3xl font-bold text-blue-600">About Me</h2>
    <p className="mt-4 text-gray-700">
      I’m a self-taught developer and a member of ACM Student Chapter and Codnex Club.
      I’ve worked on hackathon projects, websites, and love to learn modern tech stacks.
    </p>
  </div>
);

export default About;
